mapper.select:
/* gallery - pwdConfirm */
SELECT NVL((
		SELECT 1 FROM spring_gallery
		WHERE g_num = #{g_num} AND g_pwd = #{g_pwd}
	), 0) as state
FROM dual

mapper.insert:
/* gallery - galleryInsert */
SELECT spring_gallery_seq.NEXTVAL FROM dual
INSERT INTO spring_gallery (g_num, g_name, g_subject, g_content, g_thumb, g_file, g_pwd) VALUES(#{g_num}, #{g_name}, #{g_subject}, #{g_content}, #{g_thumb}, #{g_file}, #{g_pwd})

mapper.update:
/* gallery - galleryUpdate */
UPDATE spring_gallery
	SET g_subject = #{g_subject}, g_content = #{g_content}
	, g_thumb = #{g_thumb}
	, g_file = #{g_file}
	, g_pwd = #{g_pwd}
	,g_date = SYSDATE
WHERE g_num = #{g_num}
